import { Component } from "react";

class ProductMedia extends Component {

    state = {
        selectedImage: '#Media0'
    }

    selectImage = (e) => {
        console.log(e.target)
    }

    render() {
        return(
            <div className="product__media">
                <div className="product__thumbnails_wrapper">
                    <div className="product__thumbnails">
                        {
                            this.props.media.map((thumbnail, index) => {
                                return(
                                    <a href={("#Media" + index)} data-media={("#Media" + index)} onClick={ (e) => this.selectImage(e) } className={"thumbnail" + (index == 0 ? ' active': '')} >
                                        <img src={ thumbnail } />
                                    </a>
                                )
                            })
                        }
                    </div>
                </div>

                <div className="product__images">
                {
                        this.props.media.map((image, index) => {
                            return(
                                <div id={("#Media" + index)} className={"product__image" + (this.state.selectedImage == ("#Media" + index) ? ' active': '')} >
                                    <img src={ image } />
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}

export default ProductMedia
