import { Component } from "react";

class ProductMedia extends Component {

    state = {
        selectedImage: 0
    }

    selectImage = (i) => {
        this.setState({
            selectedImage: i
        })
    }

    render() {
        return(
            <div className="product__media">
                <div className="product__thumbnails_wrapper">
                    <div className="product__thumbnails">
                        {
                            this.props.media.map((thumbnail, index) => {
                                return(
                                    <a href={("#Media" + index)}  onClick={ () => this.selectImage(index) } className={"thumbnail" + (this.state.selectedImage == index ? ' active': '')} >
                                        <img src={ thumbnail } data-media={("#Media" + index)} />
                                    </a>
                                )
                            })
                        }
                    </div>
                </div>

                <div className="product__images">
                {
                        this.props.media.map((image, index) => {
                            return(
                                <div id={("#Media" + index)} className={"product__image" + (this.state.selectedImage ==  index ? ' active': '')} >
                                    <img src={ image } />
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}

export default ProductMedia
