import { useEffect, useState } from 'react'
import { useParams } from "react-router";
import products from '../products'
import ProductItem from './Global/ProductItem'
import Newsletter from './Homepage/Newsletter'
import OurStory from './Homepage/OurStory'

function Collection() {
    let { name } = useParams()
    const [collectionProducts, setCollectionProducts] = useState([])

    useEffect(() => {
        const options = {
            method: 'POST',
            headers: { 'Content-Type' : 'application/json'},
            body: JSON.stringify({ sku : id })
        }

        fetch('/collection', options)
            .then(response => response.json())
            .then(data => {
                setProduct(data.product)
                setActiveSize(data.product.variants[0].options[0])
                setActiveColor(data.product.variants[1].options[0])
            })
    }, [])

    return (
        <>
            <div className="collection__wrapper">
                <div className="collection__heading heading">All Products</div>
                <div className="collection__sort"></div>
                <div className="collection__grid">
                    {
                        products.map(product => {
                            return(
                                <ProductItem product={ product } />
                            )
                        })
                    }
                </div>
            </div>
            
            <OurStory />
            <Newsletter />
        </>
    )
}

export default Collection
