import { Component, useState } from "react";
import ProductDropdown from "./ProductDropdown";
import Quantity from './Quantity'

function ProductMeta(props) {

    const [activeOption, setActiveOption] = useState(0)
    const [quantity, setQuantity] = useState(1)

    setActiveOption = (id) => {
        this.setState({ activeOption : id })
    }

    increaseQuantity = () => {
        this.setState(prevState => ({
            quantity: prevState.quantity + 1
        }))
    }

    decreaseQuantity = () => {
        if(this.state.quantity > 1) {
            this.setState(prevState => ({
                quantity : prevState.quantity - 1
            }))
        }
    }

    return(
        <div className="product__meta">
            <div className="product__subtitle heading">{ props.product.collection }</div>
            <div className="product__title heading">{ props.product.title }</div>
            <div className="product__price heading">${ props.product.price }</div>
            <div className="product__variants">
                {
                    props.product.variants.map(variant => {
                        return(
                            <div className="product__variant">
                                <div className="product__variantLabel heading">{ variant.label }</div>
                                <div className="product__variantOptions">
                                    {
                                        variant.options.map(option => {
                                            return(
                                                <button 
                                                onClick={() => this.setActiveOption(variant.label + option)} 
                                                className={"product__variantOption heading" + (this.state.activeOption == (variant.label + option) ? ' active' : '')} 
                                                id={ variant.label + option }>{ option }</button>
                                            )
                                        })
                                    }
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <div className="seperator"></div>
            <Quantity quantity={ quantity } setQuantity ={ setQuantity } /> 
            <div className="product__formNote heading"> Free Shipping for orders over $250 </div>

            <ProductDropdown product={ props.product } />
        </div>
    )
}

export default ProductMeta
