import { Component } from "react";
import ProductDropdown from "./ProductDropdown";
import Quantity from './Quantity'

class ProductMeta extends Component {

    state = {
        activeOption: '',
        quantity: 1,
    }

    setActiveOption = (id) => {
        this.setState({ activeOption : id })
    }

    increaseQuantity = () => {
        this.setState(prevState => ({
            quantity: prevState.quantity + 1
        }))
    }

    decreaseQuantity = () => {
        if(this.state.quantity > 1) {
            this.setState(prevState => ({
                quantity : prevState.quantity - 1
            }))
        }
    }

    componentDidMount() {
        console.log(this.props.product)
    }
    
    render() {
        if(this.props.product === null) {
            return(
                <div className="loading__wrapper">
                    <div className="lds-ring"><div></div><div></div><div></div><div></div></div>
                </div>
            )
        } 

        return(
            <div className="product__meta">
                <div className="product__subtitle heading">{ this.props.product.collection }</div>
                <div className="product__title heading">{ this.props.product.title }</div>
                <div className="product__price heading">${ this.props.product.price }</div>
                <div className="product__variants">
                    {
                        this.props.product.variants.map(variant => {
                            return(
                                <div className="product__variant">
                                    <div className="product__variantLabel heading">{ variant.label }</div>
                                    <div className="product__variantOptions">
                                        {
                                            variant.options.map(option => {
                                                return(
                                                    <button 
                                                    onClick={() => this.setActiveOption(variant.label + option)} 
                                                    className={"product__variantOption heading" + (this.state.activeOption == (variant.label + option) ? ' active' : '')} 
                                                    id={ variant.label + option }>{ option }</button>
                                                )
                                            })
                                        }
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
                <div className="seperator"></div>
                <Quantity quantity={ this.state.quantity } increaseQuantity={ this.increaseQuantity } decreaseQuantity={ this.decreaseQuantity } />
                <a href="#" className="btn btn__primary full__btn">Add To Cart</a>
                <div className="product__formNote heading"> Free Shipping for orders over $250 </div>

                <ProductDropdown product={ this.props.product } />
            </div>
        )
    }
}

export default ProductMeta
