import { useEffect, useState } from "react";

function ProductMedia(props) {
    const [selectedImage, setSelectedImage] = useState(0)

    useEffect(() => {
        console.log(props.product)
    }, [])
    return(
        <div className="product__media">
            <div className="product__thumbnails_wrapper">
                <div className="product__thumbnails">
                    {
                        props.product.media[props.activeColor].map((thumbnail, index) => {
                            return(
                                <button onClick={ () => setSelectedImage(index) } className={"thumbnail" + (selectedImage == index ? ' active': '')} >
                                    <img src={ thumbnail } />
                                </button>
                            )
                        })
                    }
                </div>
            </div>

            <div className="product__images">
            {
                    props.product.media[props.activeColor].map((image, index) => {
                        return(
                            <div id={ 'Media' +  index } className={"product__image" + (selectedImage ==  index ? ' active': '')} >
                                <img src={ image } />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default ProductMedia
