import { useState } from "react";

function ProductMedia(props) {

    const [selectedImage, setSelectedImage] = useState(0)

    return(
        <div className="product__media">
            <div className="product__thumbnails_wrapper">
                <div className="product__thumbnails">
                    {
                        props.media.map((thumbnail, index) => {
                            return(
                                <a href={("#Media" + index)}  onClick={ setSelectedImage(index) } className={"thumbnail" + (selectedImage == index ? ' active': '')} >
                                    <img src={ thumbnail } />
                                </a>
                            )
                        })
                    }
                </div>
            </div>

            <div className="product__images">
            {
                    props.media.map((image, index) => {
                        return(
                            <div className={"product__image" + (selectedImage ==  index ? ' active': '')} >
                                <img src={ image } />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default ProductMedia
