import { Component } from "react";

function ProductMedia(props) {

    state = {
        selectedImage: 0
    }

    selectImage = (i) => {
        this.setState({
            selectedImage: i
        })
    }
    
    return(
        <div className="product__media">
            <div className="product__thumbnails_wrapper">
                <div className="product__thumbnails">
                    {
                        props.media.map((thumbnail, index) => {
                            return(
                                <a href={("#Media" + index)}  onClick={ () => selectImage(index) } className={"thumbnail" + (state.selectedImage == index ? ' active': '')} >
                                    <img src={ thumbnail } data-media={("#Media" + index)} />
                                </a>
                            )
                        })
                    }
                </div>
            </div>

            <div className="product__images">
            {
                    props.media.map((image, index) => {
                        return(
                            <div id={("#Media" + index)} className={"product__image" + (state.selectedImage ==  index ? ' active': '')} >
                                <img src={ image } />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default ProductMedia
