import './Styles.scss';
import AnnouncementBar from './Components/AnnouncementBar'
import Header from './Components/Header'
import Homepage from './Components/Homepage/Homepage'
import Footer from './Components/Footer';
import CartDrawer from './Snippets/CartDrawer';
import { useState } from 'react';

function App() {

  const [cartActive, toggleCart] = useState(0)

  return (
    <div className="App">
      <div className={"body__overlay overlay" + (state.cartActive ? ' cart__overlay is_visible' : '')}></div>
      <CartDrawer cartActive={ cartActive } closeCart={ toggleCart } />
      <div className="header sticky__header">
        <AnnouncementBar />
        <Header openCart={ toggleCart }/>
      </div>

      <div className="main__content">
        <Homepage />
      </div>

      <Footer />
    </div>
  );
}

export default App;
